mapper.insert:
 	insert
	 	into
 		CAPA_Company_Survey (
 			surveynum
 			, company_name
 			, memberid
 			, department
 			, q1
 			, q2
 			, q3
 	)
 	values (
 		CAPA_Company_Survey_seq.nextval
 			, #{memberid}
 			, #{company_name}
 			, #{department}
 			, #{q1}
 			, #{q2}
 			, #{q3}
 	)

mapper.delete:
delete
from
	CAPA_Company_Survey
where
	surveynum = #{surveynum}
	and memberid = #{memberid}
